import React from "react";

const Favs = () => {
  const borrarStorage = () => {
    localStorage.removeItem("favs");
    window.location.reload();
  };

  const fav = JSON.parse(localStorage.getItem("favs"));

  return (
    <>
      <h1>Dentists favs</h1>
      <div className="fav">
        {fav && (
          <>
            <img
              src="./images/doctor.jpg"
              alt=""
              width={200}
              height={200}
            />
            <p>
              {fav.name}
              <br />
              {fav.username}
            </p>
            <button className="btn2" onClick={borrarStorage}>
              Eliminar
            </button>
          </>
        )}
      </div>
    </>
  );
};

export default Favs;
